mapper.select:
SELECT

FROM task_log AS tl LEFT JOIN task_info t on tl.taskId=t.id


        AND tl.job_id = #{jobId}


        AND tl.task_id = #{taskId}


        AND tl.trigger_time  >=  #{triggerTimeStart}


        AND tl.trigger_time  <=  #{triggerTimeEnd}


        AND tl.handle_code = 200


        AND (
        tl.trigger_code NOT IN (0, 200) OR
        tl.handle_code NOT IN (0, 200)
        )


        AND tl.trigger_code = 200
        AND tl.handle_code = 0


        AND t.user_id = #{userId}


ORDER BY tl.trigger_time DESC
LIMIT #{offset}, #{pageSize}

SELECT count(1)
FROM task_log AS tl LEFT JOIN task_info t on tl.taskId=t.id


        AND tl.job_id = #{jobId}


        AND tl.task_id = #{taskId}


        AND tl.trigger_time  >=  #{triggerTimeStart}


        AND tl.trigger_time  <=  #{triggerTimeEnd}


        AND tl.handle_code = 200


        AND (
        tl.trigger_code NOT IN (0, 200) OR
        tl.handle_code NOT IN (0, 200)
        )


        AND tl.trigger_code = 200
        AND tl.handle_code = 0


        AND t.user_id = #{userId}


ORDER BY tl.trigger_time DESC
LIMIT #{offset}, #{pageSize}

SELECT

FROM task_log AS t
WHERE t.id = #{id}

SELECT count(1)
FROM task_log AS t


        AND t.handle_code = #{handleCode}

mapper.delete:
delete from task_log

    AND id NOT in(
    SELECT id FROM task_log AS t


            AND t.task_id = #{taskId}


            AND t.job_Id = #{jobId}


            AND t.trigger_time  <=  #{clearBeforeTime}



        ORDER BY t.trigger_time desc
        LIMIT 0, #{clearBeforeNum}

    )
